var entry = require('./../lib');

var port = process.argv[2] || 5555;

console.log('Checking if port ' + port + ' is mapped...');
entry.exists({ external: port }, function(err, mapping){

  if (err)
    console.log(err);
  else if (!mapping)
    console.log('Available!');
  else if (mapping.mine)
    console.log('Already mapped to me!\n', mapping);
  else
    console.log('Mapped to someone else.\n', mapping)

})
