var entry    = require('./../lib');

var external = process.argv[2] || 5555,
    internal = process.argv[3] || external,
    service  = process.argv[4] || 'My app';

console.log('Trying to map external port ' + external + ' to local ' + internal);
entry.map({
  external: external,
  internal: internal,
  name: service
}, function(err){
  if (err)
    console.log(err);
  else
    console.log('Successfully mapped external port ' + external + ' to ' + internal);
})


