var entry    = require('./');

var action   = process.argv[2] || 'map',
    external = process.argv[3] || 5555,
    internal = process.argv[4] || external,
    service  = process.argv[5] || 'My app';

console.log('Trying to ' + action + ' external port ' + external + ' to local ' + internal);
entry[action]({
  external: external,
  internal: internal,
  name: service
}, function(err){
  if (err)
    console.log(err);
  else
    console.log('Successfully ' + action + 'ped external port ' + external + ' to ' + internal);
})


